import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video containers to be present
    await page.waitForSelector('.video-container', { timeout: 5000 });

    // Extract data from all video containers
    const videoData = await page.$$eval('.video-container', (containers) => {
      return containers.map(container => {
        // Get video ID from data attribute
        const videoId = container.getAttribute('data-video-id');

        // Get like count - using multiple selector strategies
        const likeBtn = container.querySelector('button.like-btn');
        const likeCount = likeBtn ? likeBtn.querySelector('span')?.textContent?.trim() : '0';

        // Get comment count
        const commentBtn = container.querySelector('button.comment-btn');
        const commentCount = commentBtn ? commentBtn.querySelector('span')?.textContent?.trim() : '0';

        return {
          videoId,
          likeCount,
          commentCount
        };
      });
    });

    // Take screenshot after data extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extracted.png'));

    return {
      success: true,
      action: "Extracted like count, comment count, and video ID from all video containers",
      extractedData: {
        videos: videoData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
