import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the comment button to open modal
    await page.waitForSelector('button.action-btn.comment-btn');
    await page.click('button.action-btn.comment-btn');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_modal_opened.png'));

    // Wait for comment input and type the comment
    await page.waitForSelector('#commentInput');
    await page.fill('#commentInput', 'Great content!');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_comment_typed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_comment_typed.png'));

    // Click the send button
    await page.click('#commentSendBtn');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_comment_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_comment_sent.png'));

    // Verify comment was sent by checking if input is cleared
    const commentValue = await page.$eval('#commentInput', el => el.value);
    const isCommentSent = commentValue === '';

    return {
      success: isCommentSent,
      action: "Commented 'Great content!' in the comment modal and sent it",
      extractedData: {
        commentText: 'Great content!',
        commentSent: isCommentSent
      },
      screenshots,
      error: isCommentSent ? null : 'Comment input was not cleared after sending'
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

