import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click Discover menu item using data-page attribute selector
    await page.waitForSelector('[data-page="discover"]');
    await page.click('[data-page="discover"]');

    // Wait for Discover page content to load
    await page.waitForSelector('#discoverPage');
    await page.waitForTimeout(1000); // Additional wait for content rendering

    // Take screenshot after navigating to Discover page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_discover_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_discover_page.png'));

    // Find and click the first trending hashtag using multiple selector strategies
    const hashtagSelector = '.hashtag.trending:first-child, [class*="hashtag"]:first-child, [class*="trending"]:first-child';
    await page.waitForSelector(hashtagSelector, { timeout: 5000 });

    // Extract hashtag text before clicking
    const hashtagText = await page.$eval(hashtagSelector, el => el.textContent.trim());

    // Click the first trending hashtag
    await page.click(hashtagSelector);

    // Wait for navigation/update after click
    await page.waitForTimeout(2000);

    // Take final screenshot after clicking hashtag
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_hashtag_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_hashtag_clicked.png'));

    return {
      success: true,
      action: "Clicked the first trending hashtag on Discover page",
      extractedData: {
        hashtag: hashtagText,
        clickedElement: hashtagSelector
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if available
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
