import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Following page
    await page.waitForSelector('[data-page="following"]');
    await page.click('[data-page="following"]');
    await page.waitForLoadState('networkidle');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following_page.png'));

    // Wait for user profile cards to load
    await page.waitForSelector('.following-user');

    // Extract all user profile cards
    const userCards = await page.$$('.following-user');
    const extractedData = [];

    // Loop through each profile card and extract data
    for (let i = 0; i < userCards.length; i++) {
      const card = userCards[i];

      // Extract username
      const username = await card.$eval('.username', el => el.textContent.trim());

      // Extract follower count
      const followers = await card.$eval('.follower-count', el => el.textContent.trim());

      extractedData.push({ username, followers });
    }

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_extracted_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_extracted_data.png'));

    return {
      success: true,
      action: "Extracted username and follower count from all user profile cards in Following page",
      extractedData: extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

