import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Following sidebar item to navigate to following page
    await page.click('[data-page="following"]');
    await page.waitForTimeout(1000);
    await page.waitForLoadState('networkidle');

    // Wait for user profile cards to load
    await page.waitForSelector('.following-user');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following_page.png'));

    // Extract all user profile cards
    const userCards = await page.$$('.following-user');
    const usersData = [];

    // Iterate through each user card and extract data
    for (let i = 0; i < userCards.length; i++) {
      const card = userCards[i];

      // Extract username using multiple selector strategies
      const username = await card.$eval('.username, [class*="user-name"], h3, h4', el => el.textContent.trim());

      // Extract follower count using multiple selector strategies
      const followers = await card.$eval('.follower-count, [class*="follower"], [class*="count"]', el => el.textContent.trim());

      usersData.push({ username, followers });
    }

    // Take final screenshot with extracted data
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_extracted_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_extracted_data.png'));

    return {
      success: true,
      action: "Extracted username and follower count from all user profile cards on Following page",
      extractedData: { users: usersData },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
