import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click upload button to open modal
    await page.waitForSelector('#uploadBtn');
    await page.click('#uploadBtn');
    await page.waitForTimeout(1000); // Wait for modal animation

    // Wait for upload modal to appear and take screenshot
    await page.waitForSelector('#videoFileInput', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Find caption input using multiple selector strategies
    const captionInput = await page.$('input[placeholder*="caption"], #videoCaption, [id*="caption"]');

    if (!captionInput) {
      throw new Error('Caption input field not found');
    }

    // Type the caption text
    await captionInput.fill('Amazing video!');

    // Take screenshot after input
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_caption_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_caption_filled.png'));

    // Verify the text was entered correctly
    const enteredText = await captionInput.inputValue();
    if (enteredText !== 'Amazing video!') {
      throw new Error('Caption text was not entered correctly');
    }

    return {
      success: true,
      action: "Successfully entered 'Amazing video!' in the caption field of upload modal",
      extractedData: {
        captionText: enteredText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
