    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_upload_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_upload_modal_opened.png'));

    // Verify modal is open by checking for upload-related elements
    const modalElements = await page.$$eval('#videoFileInput, #videoHashtags, #videoPrivacy', 
      elements => elements.map(el => el.tagName)
    );

    return {
      success: true,
      action: "Successfully clicked upload button and opened upload modal",
      extractedData: {
        modalElementsPresent: modalElements.length,
        uploadButtonClicked: true,
        modalVisible: true
      },
