    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the upload button to be visible and enabled
    await page.waitForSelector('#uploadBtn', { state: 'visible' });
    await page.waitForTimeout(1000); // Additional stability wait

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the upload button using multiple selector strategies
    await page.click('#uploadBtn');

    // Wait for upload modal to appear - checking for multiple indicators
    await Promise.race([
      page.waitForSelector('#videoFileInput', { state: 'visible', timeout: 5000 }),
      page.waitForSelector('#videoHashtags', { state: 'visible', timeout: 5000 }),
      page.waitForSelector('#videoPrivacy', { state: 'visible', timeout: 5000 })
    ]);

    // Take screenshot after modal opens
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_modal_open.png'));

    // Verify modal is open by checking for upload-related elements
    const uploadElements = await page.$$eval('#videoFileInput, #videoHashtags, #videoPrivacy', 
      elements => elements.map(el => el.tagName + (el.id ? '#' + el.id : ''))
    );

    return {
      success: true,
      action: "Clicked upload button and successfully opened upload modal",
      extractedData: {
        modalElements: uploadElements,
        modalOpened: uploadElements.length > 0
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to open upload modal",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the upload button using multiple selector strategies
    // Strategy 1: Wait for the upload button by ID
    await page.waitForSelector('#uploadBtn', { state: 'visible', timeout: 5000 });

    // Strategy 2: Alternative selector using class and icon
    const uploadButton = await page.$('button.nav-btn i.fa-plus').catch(() => null);

    if (uploadButton) {
      await uploadButton.click();
    } else {
      // Fallback to ID selector if class selector fails
      await page.click('#uploadBtn');
    }

    // Wait for upload modal to appear (check for file input field)
    await page.waitForSelector('#videoFileInput', { state: 'attached', timeout: 3000 });

    // Take screenshot after successful click
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_upload_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_upload_modal_open.png'));

    // Verify upload modal is open by checking for upload-related elements
    const uploadModalVisible = await page.isVisible('#videoFileInput');
    const modalTitle = await page.textContent('.upload-modal h2').catch(() => 'Upload Modal');

    return {
      success: true,
      action: "Clicked upload button and opened upload modal",
      extractedData: {
        modalOpen: uploadModalVisible,
        modalTitle: modalTitle
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
