import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click upload button to open modal
    await page.waitForSelector('#uploadBtn');
    await page.click('#uploadBtn');

    // Wait for upload modal to appear
    await page.waitForSelector('input[placeholder="#hashtag1 #hashtag2"]', { state: 'visible' });

    // Take screenshot after modal opens
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Find caption input field using XPath (alternative to ID selector)
    const captionInput = await page.waitForSelector('//input[contains(@placeholder, "hashtag")]', { state: 'visible' });

    // Fill caption field with required text
    await captionInput.fill('Amazing video!');

    // Take screenshot after filling caption
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_caption_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_caption_filled.png'));

    // Verify the text was entered correctly
    const captionValue = await captionInput.inputValue();
    const extractedData = {
      captionText: captionValue,
      success: captionValue === 'Amazing video!'
    };

    return {
      success: true,
      action: "Filled caption field in upload modal with 'Amazing video!'",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
