import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and locate the first video container
    const videoContainer = await page.waitForSelector('div[data-video-id="1"]');

    // Hover over the video container to reveal controls
    await videoContainer.hover();
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_hovered.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_hovered.png'));

    // Click the volume button to reveal the slider
    const volumeButton = await videoContainer.waitForSelector('button.volume-btn');
    await volumeButton.click();
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_volume_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_volume_clicked.png'));

    // Wait for and locate the volume slider
    const volumeSlider = await videoContainer.waitForSelector('input.volume-slider');

    // Get slider dimensions
    const sliderBoundingBox = await volumeSlider.boundingBox();

    // Calculate position for 50% volume (middle of slider)
    const targetX = sliderBoundingBox.x + (sliderBoundingBox.width * 0.5);
    const targetY = sliderBoundingBox.y + (sliderBoundingBox.height / 2);

    // Drag to 50% position
    await page.mouse.move(sliderBoundingBox.x, targetY);
    await page.mouse.down();
    await page.mouse.move(targetX, targetY);
    await page.mouse.up();

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_volume_set.png'));

    // Verify the volume was set to 50%
    const volumeValue = await volumeSlider.evaluate(el => el.value);
    const extractedData = {
      finalVolume: volumeValue,
      expectedVolume: "0.5"
    };

    return {
      success: true,
      action: "Set first video volume slider to 50%",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and find the first video container with data-video-id='1'
    const videoContainer = await page.waitForSelector('[data-video-id="1"]');

    // Hover over the video container to reveal controls
    await videoContainer.hover();

    // Wait for volume slider to be visible and interactable
    const volumeSlider = await page.waitForSelector('[data-video-id="1"] input.volume-slider');

    // Set the volume to 50% by modifying the input value directly
    await page.evaluate((slider) => {
      slider.value = 0.5;
      slider.dispatchEvent(new Event('input', { bubbles: true }));
      slider.dispatchEvent(new Event('change', { bubbles: true }));
    }, volumeSlider);

    // Verify the volume was set correctly
    const currentVolume = await page.$eval('[data-video-id="1"] input.volume-slider', el => el.value);

    // Take verification screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_set.png'));

    return {
      success: true,
      action: "Set first video's volume slider to 50%",
      extractedData: {
        videoId: 1,
        currentVolume: parseFloat(currentVolume),
        volumePercentage: Math.round(parseFloat(currentVolume) * 100) + '%'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and locate the first video container with data-video-id='1'
    await page.waitForSelector('[data-video-id="1"]');
    const videoContainer = await page.$('[data-video-id="1"]');

    // Hover over video container to reveal controls
    await videoContainer.hover();
    await page.waitForTimeout(500); // Brief pause for UI animation

    // Wait for volume slider to be visible and interactable
    await page.waitForSelector('[data-video-id="1"] .volume-slider', { state: 'visible' });
    const volumeSlider = await page.$('[data-video-id="1"] .volume-slider');

    // Set volume to 50% by modifying the input value directly
    await page.$eval('[data-video-id="1"] .volume-slider', (el, value) => {
        el.value = value;
        el.dispatchEvent(new Event('input', { bubbles: true }));
        el.dispatchEvent(new Event('change', { bubbles: true }));
    }, 0.5);

    // Take screenshot after volume adjustment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_adjusted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_adjusted.png'));

    // Verify the volume was set correctly
    const currentVolume = await page.$eval('[data-video-id="1"] .volume-slider', el => el.value);

    return {
      success: true,
      action: "Set first video's volume slider to 50%",
      extractedData: {
        videoId: '1',
        volumeSetting: currentVolume
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
