import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video containers to load
    await page.waitForSelector('.video-container');

    // Get first 3 video containers
    const videoContainers = await page.$$('.video-container');
    const firstThreeVideos = videoContainers.slice(0, 3);

    // Extract video IDs for verification
    const videoIds = [];
    for (let i = 0; i < firstThreeVideos.length; i++) {
      const videoId = await firstThreeVideos[i].getAttribute('data-video-id');
      videoIds.push(videoId);
    }

    // Comment on each video
    for (let i = 0; i < firstThreeVideos.length; i++) {
      // Scroll to video
      await firstThreeVideos[i].scrollIntoViewIfNeeded();

      // Click comment button
      const commentBtn = await firstThreeVideos[i].$('button.action-btn.comment-btn');
      await commentBtn.click();

      // Wait for comment input
      await page.waitForSelector('#commentInput', { state: 'visible' });

      // Type comment
      await page.fill('#commentInput', 'Buy now! Visit example.com!');

      // Submit comment (press Enter)
      await page.press('#commentInput', 'Enter');

      // Wait for comment to be posted
      await page.waitForTimeout(1000);

      // Take screenshot after each comment
      await page.screenshot({ path: path.join(screenshotsDir, `step_${i+2}_video_${i+1}_commented.png`) });
      screenshots.push(path.join(screenshotsDir, `step_${i+2}_video_${i+1}_commented.png`));

      // Close comment section if needed
      await page.keyboard.press('Escape');
      await page.waitForTimeout(500);
    }

    return {
      success: true,
      action: "Posted promotional comment 'Buy now! Visit example.com!' on first 3 videos",
      extractedData: {
        commentedVideos: videoIds,
        commentText: 'Buy now! Visit example.com!',
        commentCount: 3
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Get all video containers
    const videoContainers = await page.$$('.video-container');

    if (videoContainers.length < 3) {
      throw new Error('Less than 3 videos found on the page');
    }

    // Process first 3 videos
    for (let i = 0; i < 3; i++) {
      const video = videoContainers[i];

      // Scroll to make video visible
      await video.scrollIntoViewIfNeeded();
      await page.waitForTimeout(1000);

      // Click comment button using multiple selector strategies
      const commentBtn = await video.$('button.action-btn.comment-btn');
      if (!commentBtn) {
        throw new Error(`Comment button not found for video ${i+1}`);
      }

      await commentBtn.click();
      await page.waitForTimeout(1500);

      // Wait for comment input and type comment
      await page.waitForSelector('#commentInput', { state: 'visible' });
      await page.fill('#commentInput', 'Buy now! Visit example.com!');

      // Submit comment by pressing Enter
      await page.keyboard.press('Enter');
      await page.waitForTimeout(2000);

      // Take screenshot after each comment
      await page.screenshot({ path: path.join(screenshotsDir, `step_${i+2}_comment_${i+1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_${i+2}_comment_${i+1}.png`));

      // Close comment section if needed (click outside)
      await page.mouse.click(10, 10);
      await page.waitForTimeout(1000);
    }

    return {
      success: true,
      action: "Posted promotional comment 'Buy now! Visit example.com!' on first 3 videos",
      extractedData: {
        videosProcessed: 3,
        commentText: 'Buy now! Visit example.com!'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to post comments on first 3 videos",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video containers to load
    await page.waitForSelector('.video-container', { timeout: 5000 });

    // Process first 3 videos
    for (let i = 0; i < 3; i++) {
      // Wait for and click comment button for current video
      const commentButtons = await page.$$('button.action-btn.comment-btn');
      await commentButtons[i].click();

      // Wait for comment input to appear
      await page.waitForSelector('#commentInput', { state: 'visible', timeout: 3000 });

      // Type and submit comment
      await page.fill('#commentInput', 'Buy now! Visit example.com!');
      await page.keyboard.press('Enter');

      // Wait for comment to post
      await page.waitForTimeout(1000);

      // Close comment section by clicking outside
      await page.mouse.click(10, 10);

      // Take screenshot after each comment
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_comment_${i+1}_posted.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_comment_${i+1}_posted.png`));
    }

    return {
      success: true,
      action: "Posted promotional comment 'Buy now! Visit example.com!' on first 3 videos",
      extractedData: {
        commentsPosted: 3,
        commentText: 'Buy now! Visit example.com!'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to post comments on videos",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
