import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and locate the first video container with data-video-id='1'
    const videoContainerSelector = 'div.video-container[data-video-id="1"]';
    await page.waitForSelector(videoContainerSelector);

    // Scroll to make sure the video is in view
    await page.$eval(videoContainerSelector, el => el.scrollIntoView());

    // Wait for the play/pause button to be visible
    const playPauseBtnSelector = `${videoContainerSelector} button.play-pause-btn`;
    await page.waitForSelector(playPauseBtnSelector, { state: 'visible' });

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the play/pause button
    await page.click(playPauseBtnSelector);

    // Wait for potential UI changes
    await page.waitForTimeout(1000);

    // Take screenshot after clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Extract button state information
    const buttonState = await page.$eval(playPauseBtnSelector, btn => {
      const icon = btn.querySelector('i');
      return {
        isPaused: icon.classList.contains('fa-play'),
        iconClass: icon.className,
        buttonText: btn.textContent.trim()
      };
    });

    return {
      success: true,
      action: "Clicked the play/pause button of the first video (data-video-id='1')",
      extractedData: {
        videoId: 1,
        buttonState: buttonState,
        timestamp: new Date().toISOString()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

