    const playPauseButton = await page.waitForSelector('xpath=//div[@data-video-id="1"]//button[contains(@class, "play-pause-btn")]');

    // Take screenshot before interaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the play/pause button
    await playPauseButton.click();

    // Wait for potential UI updates after click
    await page.waitForTimeout(1000);

    // Take screenshot after interaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Extract button state to verify action
    const buttonState = await page.$eval(
      'xpath=//div[@data-video-id="1"]//button[contains(@class, "play-pause-btn")] i',
      el => el.className
    );

    return {
      success: true,
      action: "Clicked play/pause button of first video (data-video-id='1')",
      extractedData: {
        buttonIconClass: buttonState,
        videoId: 1,
        status: buttonState.includes('pause') ? 'paused' : 'playing'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

