        const username = user.querySelector('.username')?.textContent?.trim() || 'N/A';
        const followers = user.querySelector('.follower-count')?.textContent?.trim() || 'N/A';
        return { username, followers };
      });
    });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_data_extracted.png'));

    return {
      success: true,
      action: "Extracted username and follower count from all user profile cards in Following page",
      extractedData: userData,
      screenshots,
      error: null
    };
