import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click Discover tab in sidebar
    await page.waitForSelector('div.sidebar-item[data-page="discover"]');
    await page.click('div.sidebar-item[data-page="discover"]');

    // Wait for Discover page to load
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_discover_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_discover_page.png'));

    // Wait for trending hashtags to load and click the first one
    await page.waitForSelector('.hashtag.trending', { timeout: 5000 });
    const firstHashtag = await page.$('.hashtag.trending');
    const hashtagText = await firstHashtag.textContent();
    await firstHashtag.click();

    // Wait for navigation/update and take final screenshot
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_hashtag_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_hashtag_clicked.png'));

    return {
      success: true,
      action: "Clicked the first trending hashtag on Discover page",
      extractedData: {
        hashtag: hashtagText.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

