import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the comment button of the first video
    const commentBtnSelector = "div[data-video-id='1'] button.comment-btn";
    await page.waitForSelector(commentBtnSelector, { timeout: 5000 });

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the comment button
    await page.click(commentBtnSelector);

    // Wait for comment modal to appear and take screenshot
    await page.waitForSelector('#commentInput', { timeout: 3000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_modal_opened.png'));

    // Extract comment input placeholder text to verify modal opened
    const commentPlaceholder = await page.$eval('#commentInput', el => el.placeholder);

    return {
      success: true,
      action: "Clicked comment button of first video (data-video-id='1') and opened comment modal",
      extractedData: {
        commentInputPlaceholder: commentPlaceholder,
        videoId: '1'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

