import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and locate the first video container with data-video-id='1'
    const videoContainer = await page.waitForSelector('div.video-container[data-video-id="1"]');

    // Wait for the comment button to be visible and clickable
    const commentButton = await videoContainer.waitForSelector('button.comment-btn');

    // Click the comment button
    await commentButton.click();

    // Wait for comment modal to appear (check for comment input field)
    await page.waitForSelector('#commentInput', { timeout: 5000 });

    // Take screenshot after opening comment modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_modal_open.png'));

    // Extract comment count from the button
    const commentCount = await commentButton.textContent();

    return {
      success: true,
      action: "Clicked comment button of first video (data-video-id='1') and opened comment modal",
      extractedData: {
        videoId: '1',
        commentCount: commentCount.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
