  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video container with data-video-id='1'
    const videoContainer = await page.waitForSelector('div.video-container[data-video-id="1"]');

    // Wait for like button and click it
    const likeButton = await videoContainer.waitForSelector('button.like-btn');
    await likeButton.click();

    // Wait for state change and take screenshot
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_like.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_like.png'));

    // Verify like state by checking if button has active class
    const isLiked = await likeButton.evaluate(btn => 
      btn.classList.contains('active') || 
      btn.classList.contains('liked') ||
      btn.getAttribute('aria-pressed') === 'true'
    );

    return {
      success: true,
      action: "Clicked like button on first video (data-video-id='1')",
      extractedData: {
        videoId: '1',
        liked: isLiked
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

