    screenshots.push(path.join(screenshotsDir, 'step_3_after_like.png'));

    // Verify the like state by checking if the button has active class (common pattern)
    const isLiked = await page.$eval(
      'div.video-container[data-video-id="1"] button.like-btn',
      (btn) => btn.classList.contains('active') || btn.getAttribute('aria-pressed') === 'true'
    );

    return {
      success: true,
      action: "Clicked like button on first video (data-video-id='1')",
      extractedData: {
        videoId: '1',
        isLiked: isLiked,
        timestamp: new Date().toISOString()
