import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the comment button to open modal
    await page.waitForSelector('button.action-btn.comment-btn');
    await page.click('button.action-btn.comment-btn');

    // Wait for comment modal to appear
    await page.waitForSelector('#commentInput', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_modal_open.png'));

    // Type comment in the input field
    await page.fill('#commentInput', 'Great content!');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_comment_typed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_comment_typed.png'));

    // Click send button
    await page.click('#commentSendBtn');

    // Wait for comment to be sent (input cleared or confirmation appears)
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_comment_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_comment_sent.png'));

    return {
      success: true,
      action: "Successfully wrote 'Great content!' comment and sent it",
      extractedData: {
        commentText: 'Great content!',
        status: 'Comment submitted'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click comment button using multiple selector strategies
    const commentButton = await page.waitForSelector('button.action-btn.comment-btn, [class*="comment"], [data-testid="comment-button"]');
    await commentButton.click();

    // Wait for comment modal to appear
    await page.waitForSelector('#commentInput', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_modal.png'));

    // Type comment using multiple input strategies
    const commentInput = await page.$('#commentInput');
    await commentInput.fill('Great content!');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_comment_typed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_comment_typed.png'));

    // Click send button using multiple selector strategies
    const sendButton = await page.waitForSelector('#commentSendBtn, button[type="submit"], [aria-label="Send comment"]');
    await sendButton.click();

    // Wait for comment to be posted (check for success state)
    await page.waitForTimeout(1000); // Brief pause for UI update
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_comment_posted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_comment_posted.png'));

    // Verify comment was posted by checking if input is cleared
    const inputValue = await commentInput.inputValue();
    const commentPosted = inputValue === '';

    return {
      success: true,
      action: "Successfully posted 'Great content!' comment in comment modal",
      extractedData: {
        commentPosted: commentPosted,
        finalInputValue: inputValue
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to post comment in comment modal",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the comment button using multiple selector strategies
    await page.waitForSelector('button.action-btn.comment-btn');
    await page.click('button.action-btn.comment-btn');

    // Wait for comment modal to appear and take screenshot
    await page.waitForSelector('#commentInput', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_modal_open.png'));

    // Type comment in the input field using ID selector
    await page.fill('#commentInput', 'Great content!');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_comment_typed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_comment_typed.png'));

    // Click send button using ID selector
    await page.click('#commentSendBtn');

    // Wait for comment to be processed and take final screenshot
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_comment_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_comment_sent.png'));

    // Verify comment was sent by checking if input is cleared
    const inputValue = await page.$eval('#commentInput', el => el.value);
    const commentSent = inputValue === '';

    return {
      success: true,
      action: "Successfully wrote and sent 'Great content!' comment in the comment modal",
      extractedData: {
        commentText: 'Great content!',
        commentSent: commentSent,
        inputCleared: commentSent
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
