import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Following in sidebar
    await page.waitForSelector('[data-page="following"]');
    await page.click('[data-page="following"]');

    // Wait for Following page to load
    await page.waitForSelector('.following-tab-btn');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following_page.png'));

    // Click on Users tab
    await page.click('.following-tab-btn[data-tab="users"]');

    // Wait for Users tab to activate
    await page.waitForSelector('.following-tab-btn.active[data-tab="users"]');

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_users_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_users_tab.png'));

    // Verify tab switch was successful
    const isUsersTabActive = await page.$eval('.following-tab-btn.active[data-tab="users"]', 
      el => el.getAttribute('data-tab') === 'users');

    return {
      success: true,
      action: "Switched to Users tab in Following page",
      extractedData: {
        activeTab: isUsersTabActive ? 'users' : 'unknown'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to switch to Users tab in Following page",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

