import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click Following tab in sidebar using multiple selector strategies
    const followingSelector = '.sidebar-item[data-page="following"]';
    await page.waitForSelector(followingSelector, { timeout: 5000 });
    await page.click(followingSelector);

    // Wait for Following page to load and take screenshot
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following_page.png'));

    // Click Users tab using the specific selector from the task
    const usersTabSelector = '.following-tab-btn[data-tab="users"]';
    await page.waitForSelector(usersTabSelector, { timeout: 5000 });
    await page.click(usersTabSelector);

    // Wait for Users tab content to load
    await page.waitForTimeout(1000);

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_users_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_users_tab.png'));

    // Verify success by checking if Users tab is active
    const isUsersTabActive = await page.$eval(usersTabSelector, el => 
      el.classList.contains('active') || el.getAttribute('aria-selected') === 'true'
    );

    return {
      success: true,
      action: "Successfully navigated to Following page and switched to Users tab",
      extractedData: {
        usersTabActive: isUsersTabActive,
        currentTab: 'users'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
