import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input and button to be visible
    await page.waitForSelector('#searchInput', { state: 'visible' });
    await page.waitForSelector('#searchBtn', { state: 'visible' });

    // Type '#dance' into search input
    await page.fill('#searchInput', '#dance');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_input_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_input_filled.png'));

    // Click search button
    await page.click('#searchBtn');

    // Wait for potential search results to load
    await page.waitForTimeout(1000);

    // Take screenshot after search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_search.png'));

    // Extract search results if available
    const extractedData = {};
    try {
      const results = await page.$$eval('.search-result-item', items => 
        items.map(item => ({
          type: item.getAttribute('data-type'),
          title: item.querySelector('.result-title')?.textContent,
          description: item.querySelector('.result-description')?.textContent
        }))
      );
      extractedData.searchResults = results;
    } catch (e) {
      extractedData.searchResults = 'No results container found';
    }

    return {
      success: true,
      action: "Typed '#dance' in search input and clicked search button",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

