import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the trending hashtags to load
    await page.waitForSelector('.hashtag.trending');

    // Click on the first trending hashtag
    const firstTrendingHashtag = await page.$('.hashtag.trending:first-child');
    if (firstTrendingHashtag) {
      await firstTrendingHashtag.click();

      // Take a screenshot after clicking the hashtag
      await page.screenshot({ path: path.join(screenshotsDir, 'step_2_hashtag_clicked.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_hashtag_clicked.png'));

      // Extract the current URL to verify the action
      const currentUrl = page.url();

      return {
        success: true,
        action: "Clicked on the first trending hashtag",
        extractedData: { currentUrl },
        screenshots,
        error: null
      };
    } else {
      return {
        success: false,
        action: "First trending hashtag not found",
        extractedData: {},
        screenshots,
        error: "Element not found"
      };
    }
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
