import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the comment button of the first video to be visible
    await page.waitForSelector('div.video-container[data-video-id="1"] .action-btn.comment-btn');

    // Click the comment button to open the comment modal
    await page.click('div.video-container[data-video-id="1"] .action-btn.comment-btn');

    // Take a screenshot after clicking the comment button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_modal_opened.png'));

    return {
      success: true,
      action: "Clicked the comment button to open the comment modal for the first video.",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

