import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Locate the first video comment button and click it
    await page.waitForSelector('button.action-btn.comment-btn', { timeout: 5000 });
    const commentButtons = await page.$$('button.action-btn.comment-btn');

    // Loop through the first 3 videos to add comments
    for (let i = 0; i < 3; i++) {
      await commentButtons[i].click(); // Click comment button

      // Wait for the comment input field to be available
      await page.waitForSelector('#commentInput', { timeout: 5000 });
      await page.fill('#commentInput', 'Buy now! Visit example.com!'); // Type comment

      // Submit the comment
      await page.keyboard.press('Enter'); // Assuming Enter submits the comment

      // Take a screenshot after submitting
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_comment_video_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_comment_video_${i + 1}.png`));

      // Wait for the comment button to be ready for the next video
      await page.waitForTimeout(1000); // Adding a delay to avoid rapid submissions
    }

    return {
      success: true,
      action: "Comments added to the first 3 videos successfully",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
