import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract video data
    const videoContainers = await page.$$('.video-container');
    const extractedData = [];

    for (const container of videoContainers) {
      const videoId = await container.getAttribute('data-video-id');
      const likeCount = await container.$eval('.like-btn span', span => span.textContent.trim());
      const commentCount = await container.$eval('.comment-btn span', span => span.textContent.trim());

      extractedData.push({
        videoId,
        likeCount,
        commentCount
      });
    }

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extracted.png'));

    return {
      success: true,
      action: "Extracted like count, comment count, and video IDs from all video containers.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

