import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the notifications button to open the modal
    await page.waitForSelector('#notificationsBtn');
    await page.click('#notificationsBtn');

    // Wait for the notifications modal to appear
    await page.waitForSelector('.notifications-modal', { state: 'visible' });

    // Extract the number of notifications
    const notificationCount = await page.evaluate(() => {
      const countElement = document.querySelector('.notification-count'); // Adjust selector based on actual modal structure
      return countElement ? countElement.innerText : '0';
    });

    // Take screenshot after opening the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_notifications_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_notifications_opened.png'));

    return {
      success: true,
      action: "Opened notifications modal and retrieved notification count",
      extractedData: { notificationCount },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

