import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the following users to load
    await page.waitForSelector('.following-user', { timeout: 5000 });

    // Extract usernames and follower counts
    const followersData = await page.evaluate(() => {
      const users = Array.from(document.querySelectorAll('.following-user'));
      return users.map(user => {
        const username = user.querySelector('.username-selector').innerText; // Adjust selector as needed
        const followerCount = user.querySelector('.follower-count-selector').innerText; // Adjust selector as needed
        return { username, followerCount };
      });
    });

    // Log the extracted data
    console.log(followersData);

    // Take a screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_extraction.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_extraction.png'));

    return {
      success: true,
      action: "Extracted usernames and follower counts from following users",
      extractedData: followersData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
