import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be visible and type '#dance'
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', '#dance');

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_typing.png'));

    // Wait for the search button to be visible and click it
    await page.waitForSelector('#searchBtn');
    await page.click('#searchBtn');

    // Take screenshot after clicking the search button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_clicking.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_clicking.png'));

    // Verify that the search results contain the hashtag '#dance'
    const searchResults = await page.evaluate(() => {
      return Array.from(document.querySelectorAll('.search-results .hashtag')).map(el => el.textContent);
    });

    const hashtagFound = searchResults.includes('#dance');

    return {
      success: true,
      action: "Searched for '#dance' and clicked the search button",
      extractedData: { hashtagFound },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

