import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video comment button to be available and click it
    await page.waitForSelector('.action-btn.comment-btn');
    const commentButtons = await page.$$('.action-btn.comment-btn');

    // Loop through the first three videos
    for (let i = 0; i < 3; i++) {
      // Click the comment button for the current video
      await commentButtons[i].click();
      await page.waitForSelector('#commentInput');

      // Type the comment
      const commentInput = await page.$('#commentInput');
      await commentInput.type('Buy now! Visit example.com!');

      // Click the send button (assuming it's the same button)
      await page.keyboard.press('Enter');

      // Wait for a moment to ensure the comment is submitted
      await page.waitForTimeout(1000);

      // Take a screenshot after submitting the comment
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_video_${i + 1}_comment.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_video_${i + 1}_comment.png`));

      // Refresh the comment buttons for the next video
      await page.waitForSelector('.action-btn.comment-btn');
      commentButtons = await page.$$('.action-btn.comment-btn');
    }

    return {
      success: true,
      action: "Posted the same promotional comment on the first three videos",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

