import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the volume slider to be available
    await page.waitForSelector('div.video-container[data-video-id="1"] input.volume-slider');

    // Set the volume slider to 50%
    const volumeSlider = await page.$('div.video-container[data-video-id="1"] input.volume-slider');
    await volumeSlider.evaluate(slider => slider.value = 0.5);
    await volumeSlider.dispatchEvent('input');

    // Take a screenshot after setting the volume
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_set.png'));

    return {
      success: true,
      action: "Set the volume slider of the first video to 50%",
      extractedData: {
        volume: 0.5
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the video container to be visible
    await page.waitForSelector('div.video-container[data-video-id="1"]');

    // Locate the volume slider using a more specific selector
    const volumeSliderSelector = 'div.video-container[data-video-id="1"] input.volume-slider';
    await page.waitForSelector(volumeSliderSelector);

    // Set the volume slider to 50%
    await page.evaluate((selector) => {
      const slider = document.querySelector(selector);
      slider.value = 0.5; // Set volume to 50%
      slider.dispatchEvent(new Event('input')); // Trigger input event to update the volume
    }, volumeSliderSelector);

    // Take a screenshot after setting the volume
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_set.png'));

    // Verify the volume was set correctly
    const currentVolume = await page.evaluate((selector) => {
      return document.querySelector(selector).value;
    }, volumeSliderSelector);

    return {
      success: true,
      action: "Set the first video's volume slider to 50%",
      extractedData: { currentVolume },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the volume slider to be available in the DOM
    await page.waitForSelector('div.video-container[data-video-id="1"] input.volume-slider');

    // Drag the volume slider to 50%
    const volumeSlider = await page.$('div.video-container[data-video-id="1"] input.volume-slider');
    const sliderBoundingBox = await volumeSlider.boundingBox();

    // Calculate the position to drag to for 50%
    const targetX = sliderBoundingBox.x + sliderBoundingBox.width * 0.5; // 50% position
    const targetY = sliderBoundingBox.y + sliderBoundingBox.height / 2; // Middle of the slider

    // Perform the drag action on the slider
    await page.mouse.move(sliderBoundingBox.x + 10, sliderBoundingBox.y + sliderBoundingBox.height / 2); // Move to start dragging
    await page.mouse.down(); // Press down the mouse button
    await page.mouse.move(targetX, targetY, { steps: 10 }); // Move to target position
    await page.mouse.up(); // Release the mouse button

    // Take a screenshot after adjusting the volume
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_set.png'));

    return {
      success: true,
      action: "Volume slider set to 50%",
      extractedData: { volume: 0.5 },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
