import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the video container to be visible
    await page.waitForSelector('div.video-container[data-video-id="1"]');

    // Click the fullscreen button
    await page.click('div.video-container[data-video-id="1"] button.fullscreen-btn');

    // Wait for a moment to ensure the fullscreen action is completed
    await page.waitForTimeout(1000);

    // Take a screenshot after clicking fullscreen
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_fullscreen.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_fullscreen.png'));

    return {
      success: true,
      action: "First video transitioned to fullscreen",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

