import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video to load and click the share button
    await page.waitForSelector('.video-container[data-video-id="1"]');
    await page.click('.video-container[data-video-id="1"] .action-btn.share-btn');

    // Wait for the share modal to appear and click 'Copy Link'
    await page.waitForSelector('text=Copy Link');
    await page.click('text=Copy Link');

    // Take screenshot after clicking 'Copy Link'
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_copy_link_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_copy_link_clicked.png'));

    return {
      success: true,
      action: "Clicked 'Copy Link' on the share modal of the first video",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

