import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract like count, comment count, and video-id from each video container
    const videoContainers = await page.$$('.video-container');
    const extractedData = [];

    for (const container of videoContainers) {
      const videoId = await container.getAttribute('data-video-id');
      const likeCountElement = await container.$('.like-btn span');
      const commentCountElement = await container.$('.comment-btn span');

      const likeCount = likeCountElement ? await likeCountElement.innerText() : '0';
      const commentCount = commentCountElement ? await commentCountElement.innerText() : '0';

      extractedData.push({
        videoId,
        likeCount,
        commentCount
      });
    }

    // Take a final screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extraction_complete.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extraction_complete.png'));

    return {
      success: true,
      action: "Extracted like count, comment count, and video-id from video containers",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

