import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video comment button to be visible and click it
    await page.waitForSelector('#commentInput');
    await page.type('#commentInput', 'Buy now! Visit example.com!');
    await page.click('button.action-btn.comment-btn');

    // Take screenshot after first comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_first_comment.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_first_comment.png'));

    // Wait for the second video comment button to be visible and click it
    await page.waitForSelector('div.video-container[data-video-id="2"] button.action-btn.comment-btn');
    await page.click('div.video-container[data-video-id="2"] button.action-btn.comment-btn');
    await page.type('#commentInput', 'Buy now! Visit example.com!');
    await page.click('button.action-btn.comment-btn');

    // Take screenshot after second comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_second_comment.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_second_comment.png'));

    // Wait for the third video comment button to be visible and click it
    await page.waitForSelector('div.video-container[data-video-id="3"] button.action-btn.comment-btn');
    await page.click('div.video-container[data-video-id="3"] button.action-btn.comment-btn');
    await page.type('#commentInput', 'Buy now! Visit example.com!');
    await page.click('button.action-btn.comment-btn');

    // Take screenshot after third comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_third_comment.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_third_comment.png'));

    return {
      success: true,
      action: "Successfully posted promotional comments on the first three videos",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the video feed to load
    await page.waitForSelector('.video-feed');

    // Interact with the first three videos
    for (let i = 1; i <= 3; i++) {
      // Click on the comment button for the video
      await page.click(`.video-container[data-video-id="${i}"] .action-btn.comment-btn`);
      await page.waitForSelector('#commentInput');

      // Type the promotional comment
      await page.fill('#commentInput', 'Buy now! Visit example.com!');

      // Submit the comment
      await page.keyboard.press('Enter');
      await page.waitForTimeout(1000); // Wait for a second to ensure comment is posted

      // Take a screenshot after posting the comment
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_video_${i}_comment_posted.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_video_${i}_comment_posted.png`));
    }

    return {
      success: true,
      action: "Posted promotional comments on the first three videos",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
