import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video to be available
    await page.waitForSelector('.video-container');

    // Click the share button of the first video
    await page.click('button.action-btn.share-btn');
    await page.waitForSelector('text=Copy Link'); // Wait for the 'Copy Link' option to appear

    // Click the 'Copy Link' option
    await page.click('text=Copy Link');

    // Take a screenshot after the action
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_copy_link_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_copy_link_clicked.png'));

    return {
      success: true,
      action: "Clicked 'Copy Link' on the first video's share modal",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

