import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the upload button to open the upload modal
    await page.waitForSelector('#uploadBtn');
    await page.click('#uploadBtn');
    await page.waitForLoadState('networkidle');

    // Wait for the caption input field to be visible and enter the caption
    await page.waitForSelector('#videoCaption'); // Assuming #videoCaption is the selector for the caption field
    await page.fill('#videoCaption', 'Amazing video!');

    // Take a screenshot after entering the caption
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_caption_entered.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_caption_entered.png'));

    return {
      success: true,
      action: "Entered 'Amazing video!' in the caption field of the upload modal",
      extractedData: {
        caption: await page.inputValue('#videoCaption') // Extract the caption to verify
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

