import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open upload modal
    await page.click('#uploadBtn');
    await page.waitForSelector('#videoFileInput');

    // Attempt to upload invalid files 5 times
    for (let i = 0; i < 5; i++) {
      // Select a non-video file (e.g., a text file)
      const invalidFilePath = path.join(__dirname, 'invalid_file.txt'); // Ensure this file exists
      await page.setInputFiles('#videoFileInput', invalidFilePath);

      // Wait for any potential error message to appear
      await page.waitForTimeout(1000); // Adjust timeout as necessary

      // Take a screenshot after each upload attempt
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_invalid_upload_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_invalid_upload_${i + 1}.png`));
    }

    return {
      success: true,
      action: "Attempted to upload invalid files 5 times in the upload modal",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

