import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input field to be visible
    await page.waitForSelector('#searchInput', { state: 'visible' });

    // Type '#dance' into the search input
    await page.fill('#searchInput', '#dance');

    // Wait for the search button to be visible and click it
    await page.waitForSelector('#searchBtn', { state: 'visible' });
    await page.click('#searchBtn');

    // Take screenshot after the search is performed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_search.png'));

    // Verify that the search action was successful by checking the URL or presence of search results
    const searchResultsVisible = await page.isVisible('.search-results'); // Assuming there's a search results container
    const extractedData = { searchResultsVisible };

    return {
      success: true,
      action: "Searched for '#dance' and clicked on the search button",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
