import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the volume slider to be present
    await page.waitForSelector('div.video-container[data-video-id="1"] input.volume-slider');

    // Set the volume slider to 50%
    const volumeSlider = await page.$('div.video-container[data-video-id="1"] input.volume-slider');
    await volumeSlider.evaluate(slider => slider.value = 0.5); // Set value to 0.5
    await volumeSlider.dispatchEvent(new Event('input', { bubbles: true })); // Trigger input event

    // Take screenshot after setting volume
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_set.png'));

    return {
      success: true,
      action: "Set the volume slider of the first video to 50%",
      extractedData: { volume: 0.5 },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

