import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the video container with data-video-id='1' to be visible
    await page.waitForSelector('div.video-container[data-video-id="1"]');

    // Click the fullscreen button within the video container
    await page.click('div.video-container[data-video-id="1"] button.fullscreen-btn');

    // Take a screenshot after clicking the fullscreen button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_fullscreen.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_fullscreen.png'));

    // Verify if the video is in fullscreen mode by checking the presence of a specific class or element
    const isFullscreen = await page.evaluate(() => {
      return document.fullscreenElement !== null;
    });

    return {
      success: true,
      action: "First video transitioned to fullscreen",
      extractedData: { isFullscreen },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

