import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the like button of the first video to be visible
    const likeButtonSelector = 'div.video-container[data-video-id="1"] .like-btn';
    await page.waitForSelector(likeButtonSelector);

    // Click the like button
    await page.click(likeButtonSelector);

    // Take screenshot after clicking the like button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_like_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_like_clicked.png'));

    // Extract status of the like button to verify task completion
    const likeButtonState = await page.evaluate((selector) => {
      const button = document.querySelector(selector);
      return button.classList.contains('liked');
    }, likeButtonSelector);

    return {
      success: true,
      action: "Clicked the like button on the first video.",
      extractedData: { liked: likeButtonState },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
