    screenshots.push(path.join(screenshotsDir, 'step_2_caption_entered.png'));

    // Return success response
    return {
      success: true,
      action: "Entered 'Amazing video!' into the caption field",
      extractedData: {
        caption: await page.inputValue('input#videoCaption')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
