import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container to load
    await page.waitForSelector('div.video-container[data-video-id="1"]');

    // Click the play/pause button of the first video
    await page.click('div.video-container[data-video-id="1"] button.control-btn.play-pause-btn');

    // Take a screenshot after clicking the button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_play_pause_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_play_pause_clicked.png'));

    // Extract the state of the video to verify if it was played/paused
    const isPaused = await page.evaluate(() => {
      const video = document.querySelector('div.video-container[data-video-id="1"] video');
      return video.paused;
    });

    return {
      success: true,
      action: isPaused ? "Video is paused" : "Video is playing",
      extractedData: { isPaused },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container to be visible
    await page.waitForSelector('div.video-container[data-video-id="1"]');

    // Click the play-pause button in the first video container
    const playPauseButton = await page.$('div.video-container[data-video-id="1"] .play-pause-btn');
    if (playPauseButton) {
      await playPauseButton.click();
      await page.waitForTimeout(500); // Wait for a moment to allow the action to take effect

      // Take a screenshot after clicking the button
      await page.screenshot({ path: path.join(screenshotsDir, 'step_2_play_pause_clicked.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_play_pause_clicked.png'));

      // Verify the action by checking the button icon (play or pause)
      const buttonIconClass = await playPauseButton.evaluate(el => el.querySelector('i').className);
      const isPlaying = buttonIconClass.includes('fa-pause'); // Check if it's the pause icon

      return {
        success: true,
        action: isPlaying ? "Clicked play-pause button, video is now playing." : "Clicked play-pause button, video is paused.",
        extractedData: { isPlaying },
        screenshots,
        error: null
      };
    } else {
      return {
        success: false,
        action: "Play-pause button not found.",
        extractedData: {},
        screenshots,
        error: "Button not found."
      };
    }
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

