import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the notifications button to open the modal
    await page.waitForSelector('#notificationsBtn');
    await page.click('#notificationsBtn');

    // Wait for the notifications modal to appear
    await page.waitForSelector('.notifications-modal', { state: 'visible' });

    // Extract the number of notifications
    const notificationCount = await page.evaluate(() => {
      const countElement = document.querySelector('.notification-count'); // Assuming there's an element showing the count
      return countElement ? countElement.textContent : '0';
    });

    // Take a screenshot of the notifications modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_notifications_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_notifications_modal.png'));

    return {
      success: true,
      action: "Opened notifications modal and extracted notification count",
      extractedData: { notificationCount },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

