import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Interact with the first video comment section
    await page.waitForSelector('#commentInput');
    await page.type('#commentInput', 'Buy now! Visit example.com!');
    await page.click('button.action-btn.comment-btn');
    await page.waitForTimeout(1000); // Wait for the comment to be posted

    // Take screenshot after posting the first comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_first_comment.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_first_comment.png'));

    // Interact with the second video comment section
    await page.waitForSelector('.video-container[data-video-id="2"] button.action-btn.comment-btn');
    await page.click('.video-container[data-video-id="2"] button.action-btn.comment-btn');
    await page.waitForSelector('#commentInput');
    await page.type('#commentInput', 'Buy now! Visit example.com!');
    await page.click('button.action-btn.comment-btn');
    await page.waitForTimeout(1000); // Wait for the comment to be posted

    // Take screenshot after posting the second comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_second_comment.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_second_comment.png'));

    // Interact with the third video comment section
    await page.waitForSelector('.video-container[data-video-id="3"] button.action-btn.comment-btn');
    await page.click('.video-container[data-video-id="3"] button.action-btn.comment-btn');
    await page.waitForSelector('#commentInput');
    await page.type('#commentInput', 'Buy now! Visit example.com!');
    await page.click('button.action-btn.comment-btn');
    await page.waitForTimeout(1000); // Wait for the comment to be posted

    // Take screenshot after posting the third comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_third_comment.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_third_comment.png'));

    return {
      success: true,
      action: "Posted promotional comments on the first three videos.",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
