    screenshots.push(path.join(screenshotsDir, 'step_2_after_caption.png'));

    return {
      success: true,
      action: "Entered 'Amazing video!' in the caption field of the upload modal.",
      extractedData: { caption: 'Amazing video!' },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
