import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video containers to be loaded
    await page.waitForSelector('.video-container');

    // Extract data from each video container
    const videoData = await page.evaluate(() => {
      const videos = Array.from(document.querySelectorAll('.video-container'));
      return videos.map(video => {
        const likeCount = video.querySelector('.like-btn span') ? video.querySelector('.like-btn span').innerText : null;
        const commentCount = video.querySelector('.comment-btn span') ? video.querySelector('.comment-btn span').innerText : null;
        const videoId = video.getAttribute('data-video-id');
        return {
          likeCount,
          commentCount,
          videoId
        };
      });
    });

    // Log extracted data for verification
    console.log(videoData);

    // Take a screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extracted.png'));

    return {
      success: true,
      action: "Successfully extracted like count, comment count, and video IDs from video containers.",
      extractedData: videoData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
