import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the comment input field to be visible
    await page.waitForSelector('#commentInput');

    // Type 'Great content!' into the comment input field
    await page.fill('#commentInput', 'Great content!');

    // Click the send button to submit the comment
    await page.click('#commentSendBtn');

    // Wait for the comment to be sent (you may need to adjust this based on the actual behavior of the site)
    await page.waitForTimeout(2000); // Adjust timeout as necessary

    // Take a screenshot after submitting the comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_submitted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_submitted.png'));

    return {
      success: true,
      action: "Comment 'Great content!' submitted successfully",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

